select Id, Name, Action, Sum(res)/Sum(win) as diff from (Select l.rowid as Id, l.name, l.action, ((result + 1.0)/2.0) as res, count(a.performed) as win from L_GameResult as g, L_PlayRecord as r , L_ActionState as a, L_ActionList as l where g.rowid = r.gameid and a.HistoryId = r.rowid and a.Performed == 'True' and l.rowid = a.actionid group by l.name, l.action, result) group by Name, Action order by diff desc